ode.'"' : '';		$option .= ($publickey !='') ? ' data-publickey="'.$publickey.'"' : '';		$option .= ($widgetname !='') ? ' data-widgetname="'.$widgetname.'"' : '';		$option .= ($geo !='') ? ' data-geo="'.$geo.'"' : '';		$option .= ($urlprefix !='') ? ' data-urlprefix="'.$urlprefix.'"' : '';		$option .= ($categories !='') ? ' data-categories="'.$categories.'"' : '';		/**Supported ad networks: Yieldmo**/		$option .= ($ymid !='') ? ' data-ymid="'.$ymid.'"' : '';		/**Supported ad networks: SmartAdServer**/		$option .= ($call !='') ? ' data-call="'.$call.'"' : '';		$option .= ($site !='') ? ' data-site="'.$site.'"' : '';		$option .= ($page !='') ? ' data-page="'.$page.'"' : '';		$option .= ($format !='') ? ' data-format="'.$format.'"' : '';		$option .= ($target !='') ? ' data-target="'.$target.'"' : '';		/**Supported ad networks: Revcontent**/		$option .= ($id !='') ? ' data-id="'.$id.'"' : '';		$option .= ($wrapper !='') ? ' data-wrapper="'.$wrapper.'"' : '';		$option .= ($endpoint !='') ? ' data-endpoint="'.$endpoint.'"' : '';		$option .= ($ssl !='') ? ' data-ssl="'.$ssl.'"' : '';		$option .= ($testing !='') ? ' data-testing="'.$testing.'"' : '';		if($source == 'yes'){			$data = '<amp-ad'.$option.'>'.do_bbcodes($content).'</amp-ad>';			$data .= JFactory::getDocument()->addCustomTag('<script async custom-element="amp-ad" src="https://cdn.ampproject.org/v0/amp-ad-0.1.js"></script>');		}else{			$data = '<amp-ad'.$option.'>'.do_bbcodes($content).'</amp-ad>';		}		return $data;	}			add_bbcodes( 'amp-ad', 'ampad_sc' );}if(!function_exists('ampembed_sc')) {	function ampembed_sc( $atts, $content="") {		extract(bbcodes_atts(array(				'width' => '',				'height' => '',				'layout' => '',				'publisher' => '',				'mode' => '',				'article' => '',				'source' => '',				'placement' => ''		 ), $atts));				$option = ($width !='') ? ' width='.$width : '';		$option .= ($height !='') ? ' height='.$height : '';		$option .= ($layout !='') ? ' 