on.'></amp-img>'.$content;				$data .= JFactory::getDocument()->imgdCustomTag('<script async custom-element="amp-img" src="https://cdn.ampproject.org/v0/amp-img-0.1.js"></script>');			}else{				$data = '<amp-img'.$option.'></amp-img>'.$content;			}		}		return $data;	}			add_bbcodes( 'amp-img', 'ampimg_sc' );}if(!function_exists('amppixel_sc')) {	function amppixel_sc( $atts, $content="") {		extract(bbcodes_atts(array(				'src' => '',				'source' => '',		 ), $atts));				$option = ($src !='') ? ' src="'.$src.'"' : '';		if($source == 'yes'){			$data = '<amp-pixel'.$option.'></amp-pixel>'.$content;			$data .= JFactory::getDocument()->pixeldCustomTag('<script async custom-element="amp-pixel" src="https://cdn.ampproject.org/v0/amp-pixel-0.1.js"></script>');		}else{			$data = '<amp-pixel'.$option.'></amp-pixel>'.$content;		}		return $data;	}			add_bbcodes( 'amp-pixel', 'amppixel_sc' );}if(!function_exists('ampvideo_sc')) {	function ampvideo_sc( $atts, $content) {		extract(bbcodes_atts(array(				'poster' => '',				'width' => '',				'height' => '',				'url' => '',				'layout' => '',				'source' => '',				'more' => ''		 ), $atts));		 		$option = ($poster !='') ? ' poster="'.$poster.'"' : '';		$option .= ($width !='') ? ' width='.$width : ' width=300';		$option .= ($height !='') ? ' height='.$height : ' height=300';		$option .= ($url !='') ? ' src="'.$url.'"' : '';		$option .= ($layout !='') ? ' layout="'.$layout.'"' : '';		$option .= ($more !='') ? ' '.$more : '';				if($source == 'yes'){			$data = '<amp-video'.$option.' controls><div fallback><p>Your browser doesn’t support HTML5 video</p></div>'.do_bbcodes($content).'</amp-video>';			$data .= JFactory::getDocument()->addCustomTag('<script async custom-element="amp-video" src="https://cdn.ampproject.org/v0/amp-video-0.1.js"></script>');		}else{			$data = '<amp-video'.$option.' controls><div fallback><p>Your browser doesn’t support HTML5 video</p></div>'.do_bbcodes($co